import React, { Component } from 'react';
import axios from 'axios';
import Button from '../../components/Button';
import Input from '../../components/Input';
import Footer from '../../components/Footer';
import GlobalStyle from './styles';
import Space from '../../components/Space';
import Input2Mask from '../../components/InputMask';


class AtualizarPerfil extends Component {


    constructor(props){

        super(props);

        this.state = {
            profileImage: '',
            userData: {
                nomeCompleto: localStorage.getItem('nomeCompleto'),
                telefone: localStorage.getItem('telefone'),
                email: localStorage.getItem('email'),
                idUsuario: localStorage.getItem('idUsuario'),
                descricao: localStorage.getItem('descricao')
            }
        }

        // this.url = 'http://localhost:3001/registrar' Colocar a Url do back aqui

        this.userData = {
            telefone: localStorage.getItem("telefone"),
            descricao: localStorage.getItem("descricao"),
            location: ''
        }


    }

    // constructor(props) {
    //     super(props)

    //     this.state = {
    //         descricao: '',
    //         phone: '',
    //         password: '',
    //         errPhone: ''

    //     }


    // }
    validate() {
        var isCorrect = true

        if (this.state.phone.replace(/\D/gim, '').length < 10) {
            isCorrect = false
            this.setState({
                errPhone: "Telefone InvÃ¡lido"
            })
        }

        if (isCorrect) {

            this.setState({
                errPhone: ""
            })
            this.userData.descricao = this.state.descricao;
            this.userData.telefone = this.state.phone.replace(/\D/gim, '');

        }
        return isCorrect
    }


    handleDescricaoChange = event => {
        this.setState({
            description: event.target.value
        })
    }


    handlePhoneChange = event => {
        this.setState({
            phone: event.target.value
        })
    }


    handleSubmit = event => {
        event.preventDefault()
        var valid = this.validate()
        const headers = {
            'content-type': 'application/json',
        }

        if (valid) {
            axios.post(this.url, this.userData, headers)
                .then(response => {
                    this.props.handleLogin(response.data.dados);
                    localStorage.setItem("token",response.data.token)
                    localStorage.setItem("telefone",response.data.dados.telefone)
                    localStorage.setItem("descricao", response.data.dados.descricao)

                    this.props.history.push('/');
                })
                .catch(error => {
                    console.log(error)
                })

        }
        this.render()

    }

    render() {
        const { phone, errPhone, descricao, location } = this.state
        return (
            <>
                <GlobalStyle />
                <main>
                    <h1>ATUALIZAR PERFIL</h1>
                    <form onSubmit={this.handleSubmit}>
                        <p>Atualize seus dados e aumente suas chances de ser contratado!</p>
                        <Space />
                        <label>DescriÃ§Ã£o:
                        <Input
                                type="textArea"
                                id="descricao"
                                name="descricao"
                                placeholder="Fale sobre os seus serviÃ§os..."
                                value={descricao}
                                onChange={this.handleDescricaoChange}
                            />
                        </label>
                        <Space />

                        <Space />
                        <label>Telefone:
                        <Input2Mask
                                type="text"
                                id="phone"
                                name="phone"
                                alwaysShowMask="true"
                                mask="(99) 99999-9999"

                                value={phone}
                                onChange={this.handlePhoneChange}
                            />
                            {/* <Alert>{errPhone}</Alert> */}
                        </label>
                        <Space />

                        <Button>Enviar</Button>
                    </form>
                </main>
                <Footer orange />
            </>
        )
    }
}

export default AtualizarPerfil
