import React, { Component } from 'react';
// import PageDefault from '../../pages/PageDefault';
import Footer from '../../components/Footer';
import Card from '../../components/Card';
import Space from '../../components/Space';
import axios from 'axios';
class PerfilUsuario extends Component {

    constructor(props) {
        super(props)

        this.state = {
            posts: '',
            descripition: '',
            profileImage: '',
            locale: ''
        }
    }
    componentDidMount() {
        // var url = 'http://localhost:3001/' + localStorage.getItem('idUsuario') + '/publicacoes';
        // console.log(url);
        axios.get('http://localhost:3001/:' + localStorage.getItem('idUsuario') + '/publicacoes')
            .then(response => {
                // this.props.handleLogin(response.data.dados);
                console.log(response)
            })
            .catch(error => {
                console.log(error)
                this.setState({
                    posts: 'Ainda não existem imagens no seu perfil!'
                })
                console.log(this.state.posts)
            })
    }
    render() {
        return (
            <>
                <main>
                    <h1>PerfilUsuario</h1>
                    <br/>
                    <h1>Olá {localStorage.getItem("name")}</h1>
                    <br/>
                    <Card horizontal width="100%">Email: {localStorage.getItem("email")}</Card>
                    <Space/>
                    <Card horizontal width="100%">Telefone: {localStorage.getItem("telefone")}</Card>
                </main>
                <Footer/>
            </>
        )
    }
}

export default PerfilUsuario;
