import React, { Component } from 'react';
// import PageDefault from '../../pages/PageDefault';
import Footer from '../../components/Footer';
import Card from '../../components/Card';
import Space from '../../components/Space';

class PerfilUsuario extends Component {

    componentDidMount() {
        var url = 'http://localhost:3001/:' + localStorage.getItem('idUsuario') + '/publicacoes'
        axios.post(url, this.userData)
        .then(response => {
            this.props.handleLogin(response.data.dados);
            // console.log(response)
            localStorage.setItem("token",response.data.token)
            localStorage.setItem("loggedIn","LOGGED_IN")
            localStorage.setItem("idUsuario",response.data.dados.idUsuario)
            localStorage.setItem("name",response.data.dados.nomeCompleto)
            localStorage.setItem("email",response.data.dados.email)
            localStorage.setItem("telefone",response.data.dados.telefone)
            this.props.history.push('/');
        })
        .catch(error => {
            console.log(error)
            this.setState({
                err: "O usuário não existe ou a senha está incorreta"
            })
        })
    }
    render() {
        return (
            <>
                <main>
                    <h1>PerfilUsuario</h1>
                    <br/>
                    <h1>Olá {sessionStorage.getItem("name")}</h1>
                    <br/>
                    <Card horizontal width="100%">Email: {sessionStorage.getItem("email")}</Card>
                    <Space/>
                    <Card horizontal width="100%">Telefone: {sessionStorage.getItem("telefone")}</Card>
                </main>
                <Footer/>
            </>
        )
    }
}

export default PerfilUsuario;
