import styled from 'styled-components';

export const Container = styled.div`

    padding: 30px 50px;
    margin: 30px;
    box-shadow: 2px 4px 4px;
    align-items: center;
    width: 100%;
    background: #FFFFFF;
    border-radius: 30px;

h1, h2, h3{
text-align: center;
padding: 15px 0;
}
p {
    text-align: justify;
    padding: 6px 0;
}
ul, ol{
    padding-left: 40px;
    text-align: justify;
}

`;

export default Container;
