import styled from 'styled-components';

const Box = styled.div`
    width: 100%;
    select {
        padding: 10px 0px;
        background-color: #ffffff;
        font-size: 20px;
        font-weight: bold;
        border-radius: 10px;
        box-shadow: 0px 10px 4px rgba(0, 0, 0, 0.25);
    }

`;

export default Box;
