import React,{Component} from 'react';
import axios from 'axios';
import Button from '../../components/Button';
import Input from '../../components/Input';
import Footer from '../../components/Footer';
import GlobalStyle from './styles';
import Space from '../../components/Space';

class Cadastro extends Component {
// Essa classe Cadastro funciona como página de cadastro, possui 3 atributos state,url e UserData.
// state consiste no conjunto de estados da tela, desde os inputs do usuario até os erros possiveis
// url é a rota que será usada para requisições ao back
// UserData são os dados do novo usuário já validados, separados para o envio da requisição

    constructor(props){
        super(props)



        this.state = {
            name: '',
            email:'',
            password:'',
            confirm_password:'',
            errPassword:'',
            errConfirmPassword: '',
            terms: false,
            errTerms: ''

        }

        this.url = 'http://localhost:3001/registrar'// Colocar a Url do back aqui

        this.userData = {
            nomeCompleto : '',
            email : '',
            senha : '',
            telefone: '40018922'
        }
    }

    validate(){
        var isCorrect = true
        if (this.state.password.length < 6){
            isCorrect = false
            this.setState({
                errPassword: "Sua senha deve possuir no minimo 6 digitos!"
            })

        }
        if (this.state.password !== this.state.confirm_password) {
            isCorrect = false
            this.setState({
                errConfirmPassword: "As senhas não correspodem"
            })
        }
        if(!this.state.terms){
            isCorrect = false
            this.setState({
                errTerms: "Você precisa concordar com os termos de uso!"
            })
        }
        if(isCorrect){
            this.setState({
                errConfirmPassword : ''
            })
            this.setState({
                errPassword : ''
            })
            this.userData.nomeCompleto = this.state.name;
            this.userData.email = this.state.email;
            this.userData.senha = this.state.password;

        }
        return isCorrect
    }

    handleTermsClicked = () => {
        this.setState({
            terms: !this.state.terms
        })
    }

	handleNameChange = event => {
		this.setState({
			name: event.target.value
		})
	}

	handleEmailChange = event => {
		this.setState({
			email: event.target.value
		})
	}

	handlePasswordChange = event => {
		this.setState({
			password: event.target.value
		})
	}

	handleConfirmPasswordChange = event => {
		this.setState({
			confirm_password: event.target.value
		})
	}

	handleSubmit = event => {
		event.preventDefault()
        var valid = this.validate()
        const headers = {
            'content-type': 'application/json',      
        }
        console.log(this.userData)
        if (valid){
            axios.post(this.url,this.userData, headers)
                .then( response => {
                    console.log(response)
                    this.props.history.push('/');
                } )
                .catch( error => {
                    console.log(error)
                } )

        }
        this.render()

	}

	render() {
        const {name,email,password,confirm_password,errPassword,errConfirmPassword,terms,errTerms} = this.state
		return (
            <>
            <GlobalStyle/>
            <div id="content">
            <h1>CADASTRE-SE</h1>
            <p>Cadastre-se para ter acesso a melhor plataforma do mercado!</p>
			<form onSubmit={this.handleSubmit}>
                <Space/>
					<label>Nome:
                        <Input
                            type="text"
                            id="name"
                            name="name"
                            placeholder = "Digite seu nome" 
                            value={name}
                            onChange={this.handleNameChange}
                        />
                    </label>
                <Space/>
					<label>Email:
                        <Input
                            type="email"
                            id="email"
                            name="email"
                            placeholder = "Digite seu email" 
                            value={email}
                            onChange={this.handleEmailChange}
                        />
                    </label>
                <Space/>
					<label>Senha:
                        <Input
                            type="password"
                            id="password"
                            name="password"
                            placeholder = "Digite sua senha" 
                            value={password}
                            onChange={this.handlePasswordChange}
                        />
                    </label>
                    <div>{errPassword}</div>
                <Space/>
					<label>Confirme sua senha:
                        <Input
                            type="password"
                            id="confirm_password"
                            name="confirm_password"
                            placeholder = "Confirme sua senha" 
                            value={confirm_password}
                            onChange={this.handleConfirmPasswordChange}
                        />
                    </label>
                    <div>{errConfirmPassword}</div>

                <Space/>
                <label> 
                <input id = "user-terms" type = "checkbox" name = "terms" onClick = {this.handleTermsClicked} value={terms}/>
                Li e aceito os <b>termos de uso</b>
                </label>
                <div>{errTerms}</div>

                <div>
                <Space/>
                <Button>Enviar</Button>
                </div>
			</form>
            </div>
            <Footer orange />
            </>
		)
	}
}

export default Cadastro
