import React, { Component } from 'react';
import Footer from '../../components/Footer';
import Card from '../../components/Card';
import Space from '../../components/Space';
import ProfileCard from '../../components/ProfileCard';
import CardGroup from '../../components/CardGroup';
import imgProfileDefault from '../../assets/profileDefault.png';
import axios from 'axios';

class PerfilUsuario extends Component {
    constructor(props){
        super(props);

        this.state = {
            posts: {},
            profileImage: '',
            userData: {
                nomeCompleto: localStorage.getItem('nomeCompleto'),
                telefone: localStorage.getItem('telefone'),
                email: localStorage.getItem('email'),
                idUsuario: localStorage.getItem('idUsuario'),
                // descricao: localStorage.getItem('descricao')
            }
        }
    }

    componentDidMount = () => {
        const url = 'http://localhost:3001/' + localStorage.getItem('idUsuario') + '/publicacoes'
        console.log(url)
        axios.get(url,{
            headers:{
                'authorization': localStorage.getItem('token')
            }
        })
            .then(response => {
                // [{"idPublicacao":1,"descricao":"Fiz isso, isso e aquilo em 2 dias.","pubCriadaEm":"2020-11-27T12:40:15.238Z","idUsuario":170,"idImagem":1,"nomeImagem":"fakePubImage1.jpg"},{"idPublicacao":136,"descricao":"Fiz isso, isso e aquilo em 2 dias.","pubCriadaEm":"2020-11-27T12:40:15.238Z","idUsuario":170,"idImagem":136,"nomeImagem":"fakePubImage136.jpg"},{"idPublicacao":232,"descricao":"Fiz isso, isso e aquilo em 2 dias.","pubCriadaEm":"2020-11-27T12:40:15.238Z","idUsuario":170,"idImagem":232,"nomeImagem":"fakePubImage232.jpg"}]
                console.log(response);

                let imagem = {};
                let aux;

                for( var i in response.data){
                    // let [id, nomeImagem] = response.data[i];
                    console.log(response.data[i].nomeImagem);
                    let id = response.data[i].idPublicacao;
                    let nomeImagem = response.data[i].nomeImagem;
                    console.log("test:",{imagem, ...{id, nomeImagem}});
                    imagem = {...imagem, ...{id, nomeImagem}};
                    console.log('%cMyProject%cline:46%cimagem', 'color:#fff;background:#ee6f57;padding:3px;border-radius:2px', 'color:#fff;background:#1f3c88;padding:3px;border-radius:2px', 'color:#fff;background:rgb(252, 157, 154);padding:3px;border-radius:2px', imagem)
                }
                this.setState({
                    posts: imagem,
                })
                console.log(this.state.posts);

            })
            .catch(error => {
                console.log(error);
            })
    }

    render() {
        return (
            <>
                <main>
                    <Space/>
                    <h2>{localStorage.getItem("name")}</h2>
                    <ProfileCard
                        src={imgProfileDefault}
                        title="Descrição:"
                        description={localStorage.getItem("descricao") || "Sem descrição"}
                    />
                    <Space size="10px"/>
                    <CardGroup>
                        <Card>
                            <p><strong>Categoria:</strong><br/>
                            {localStorage.getItem("categoria") || "Não definido"}</p>
                        </Card>
                        <Card>
                            <p><strong>Telefone:</strong><br/>
                            {localStorage.getItem("telefone") || "Não definido"}</p>
                        </Card>
                        <Card>
                            <p><strong>Região:</strong><br/>
                            {localStorage.getItem("localidade") == null || "Não definido"}</p>
                        </Card>
                    </CardGroup>
                </main>
                <Footer/>
            </>
        )
    }
}

export default PerfilUsuario;
